import React, { useState } from "react";
import classes from "./Table.module.css"
import ActivityItem from "./ActivityItem";

const Table = (datas) => {
    const [lastId, setId] = useState(0)

    let defaultMargin=230;
    let defaultLeftMargin=540;
    let marginStep=30;

    const [margin, setMargin] = useState(defaultMargin);

    const changeMargin = () => {
        margin += marginStep;
        console.log(margin.toString() + 'px');
        document.getElementById('1').style.top=(margin.toString() + 'px');
    }

    const [actDatas, setActDatas] = useState([{'id' : '0xx', label: 'test activity'}]);


    const addActivity = () => {
        console.log('here')
        let tmp_id = lastId.toString() + 'xxx';
        console.log(tmp_id)
        const newActivity = {
            label: 'Added',
            id: tmp_id,
            top: margin.toString()
        }

        console.log(lastId)
        console.log(margin);

        setActDatas([...actDatas, newActivity]);
        setMargin(margin + marginStep);
        setId(lastId + 1);
    }


    return(
        <div className={classes.global}>
            <div className={classes.table}>
                { actDatas.map((data) => 
                <ActivityItem data={data} key={data.id}/>) }

            </div>
            <button onClick={addActivity}>Click me!</button>


        </div>
        )
}

export default Table;
