import React from "react";
import { useState } from "react";

// функциональный подход к написанию компонент, самый популярный
const Counter = () => {
    const [views, setViews] = useState(10);

    function increment() {
        setViews(views + 1);
        console.log(views);
    }

    function decrement() {
        setViews(views - 1);
        console.log(views);
    }

    // возвращаем какой-то элемент окна
    return(
        <div>
            <h1>{views}</h1>
            <button onClick={increment}>Increment</button>
            <button onClick={decrement}>Decrement</button>
        </div>
    )

}

// экспортируем, чтобы она была видна наверху
export default Counter;
