import React, {useState} from "react";

function App() {

  // views - элемент, состояние которого мы хотим менять
  // setViews - функция, с помощью которой мы хотим это сделать
  // 10 - initial value
  const [views, setViews] = useState(10);

  function increment() {
    setViews(views + 1);
    console.log(views)
  }


  function decrement() {
    setViews(views - 1)
    console.log(views)
  }

  return (
    <div className="App">
      <h1>{views}</h1>
      <button onClick={increment}>Increment</button>
      <button onClick={decrement}>Decrement</button>
    </div>
  );
}

export default App;
